			{				if(set_value('code') != '' && set_value('discount') > 0 && set_value('discount') <= 100)				{					$id = $this->Admin_mo->set('coupons', array('user'=>$user->id,'code'=>set_value('code'),'discount'=>set_value('discount'),'time'=>''));					if(empty($id))					{						$_SESSION['time'] = time(); $_SESSION['message'] = 'somthingwrong';					}					else					{						$this->Admin_mo->set('notifications', array('type'=>'coupon','user'=>$this->session->userdata('uid'),'target'=>$user->id,'action'=>'لقد حصلت على خصم من حساب رقم '.$user2->code.' تضغط على http://nozol.com/coupon_active/'.set_value('code').' للتفعيل','time'=>time()));						$_SESSION['time'] = time(); $_SESSION['message'] = 'success';					}				}				else				{					$_SESSION['time'] = time(); $_SESSION['message'] = 'inputnotcorrect';				}				redirect('users/customers', 'refresh');			}			else			{				$data['title'] = 'customers';				$data['admessage'] = 'youhavenoprivls';				$data['system'] = $this->mysystem;				$this->lang->load($this->loginuser->lang, $this->loginuser->lang);				$this->config->set_item('language', $this->loginuser->lang);				$this->load->view('headers/customers',$data);				$this->load->view('sidemenu',$data);				$this->load->view('topmenu',$data);				$this->load->view('admin/messages',$data);				$this->load->view('footers/customers');				$this->load->view('messages');			}		}		else		{			$data['title'] = 'customers';			$data['admessage'] = 'youhavenoprivls';			$data['system'] = $this->mysystem;			$this->lang->load($this->loginuser->lang, $this->loginuser->lang);			$this->config->set_item('language', $this->loginuser->lang);			$this->load->view('headers/customers',$data);			$this->load->view('sidemenu',$data);			$this->load->view('topmenu',$data);			$this->load->view('admin/messages',$data);			$this->load->view('footers/customers');			$this->load->view('messages');		}		}		else		{			$data['title'] = 'customers';			$data['admessage'] = 'youhavenoprivls';			$data['system'] = $this->mysystem;			$this->lang->load($this->loginuser->lang, $this->loginuser->lang);			$this->config->set_item('language', $this->loginuser->lang);			$this->load->view('headers/customers',$data);			$this->load->view('sidemenu',$data);			$this->load->view('topmenu',$data);			$this->load->view('admin/messages',$data);			$this->load->view('footers/customers');			$this->load->view('messages');		}	}	public function name_validation()	{		if($_POST['val'] != '' && $_POST['val'] != ' ')		{			 			if(!preg_match('/[^0-9 ]/',$_POST['val'])) echo 5;			//elseif(strlen($_POST['val']) < 5) echo 2;			elseif(strlen($_POST['val']) > 255) echo 3;			else echo 1;		}		else echo 0;	}	public function email_validation()	{		if($_POST['val'] != '' && $_POST['val'] != ' ')		{			if(isset($_POST['id']) && $_POST['id'] != '') $where = 'id <> '.$_POST['id'].' and';			else $where = '';						if(filter_var($_POST['val'], FILTER_VALIDATE_EMAIL) === false) echo 2;			elseif($this->Admin_mo->exist('users','where '.$where.' email like "'.$_POST['val'].'"','')) echo 4;			else echo 1;		}		else echo 0;	}		public function password_validation()	{		if($_POST['val1'] != '')		{			if(strlen($_POST['val1']) < 6) echo 2;			elseif(strlen($_POST['val1']) > 255) echo 3;			elseif($_POST['val2'] != '' && $_POST['val2'] != ' ' && $_POST['val1'] != $_POST['val2']) echo 4;			elseif($_POST['val2'] != '' && $_POST['val2'] != ' ' && $_POST['val1'] == $_POST['val2']) echo 5;			else echo 1;		}		else echo 0;	}		public function cnfpassword_validation()	{		if($_POST['val1'] != '' && $_POST['val2'] != '')		{			if($_POST['val1'] != $_POST['val2']) echo 4;			else echo 1;		}		else echo 0;	}		public function username_validation()	{		if($_POST['val'] != '' && $_POST['val'] != ' ')		{			if(isset($_POST['id']) && $_POST['id'] != '') $where = 'id <> '.$_POST['id'].' and';			else $where = '';						if(preg_match('/[^a-z]/',$_POST['val'])) echo 2;			elseif(strlen($_POST['val']) < 5) echo 3;			elseif(strlen($_POST['val']) >= 255) echo 5;			elseif($this->Admin_mo->exist('users','where '.$where.' username like "'.$_POST['val'].'"','')) echo 4;			else echo 1;		}		else echo 0;	}	public function add()	{		if(strpos($this->loginuser->privileges, ',uadd,') !== false && in_array('U',$this->sections))		{		$data['admessage'] = '';		$data['system'] = $this->mysystem;		$this->lang->load($this->loginuser->lang, $this->loginuser->lang);		$this->config->set_item('language', $this->loginuser->lang);		$data['usertypes'] = $this->Admin_mo->getwhere('usertypes',array('active'=>'1','id != '=>'1'));		$data['langs'] = $this->Admin_mo->getwhere('langs',array('active'=>'1'));						$employees = $this->Admin_mo->get('users'); 						$this->load->view('headers/user-add',$data);		$this->load->view('sidemenu',$data);		$this->load->view('topmenu',$data);		$this->load->view('admin/user-add',$data);		$this->load->view('footers/user-add',$data);		$this->load->view('messages');		}		else		{		$data['title'] = 'users';		$data['admessage'] = 'youhavenoprivls';		$data['system'] = $this->mysystem;		$this->lang->load($this->loginuser->lang, $this->loginuser->lang);		$this->config->set_item('language', $this->loginuser->lang);		$this->load->view('headers/users',$data);		$this->load->view('sidemenu',$data);		$this->load->view('topmenu',$data);		$this->load->view('admin/messages',$data);		$this->load->view('footers/users');		$this->load->view('messages');		}	}		public function create()	{		if(strpos($this->loginuser->privileges, ',uadd,') !== false && in_array('U',$this->sections))		{		    $this->lang->load($this->loginuser->lang, $this->loginuser->lang);		$this->config->set_item('language', $this->loginuser->lang);		$this->form_validation->set_error_delimiters('<div class="alert alert-danger alert-dismissible fade in" role="alert"><button type="button" class="close" data-dismiss="alert" aria-label="Close"><sp