uired');			$this->form_validation->set_rules('lang', 'lang:lang' , 'trim|required');			if($this->form_validation->run() == FALSE)			{				//$data['admessage'] = 'validation error';				//$_SESSION['time'] = time(); $_SESSION['message'] = 'inputnotcorrect';				$data['system'] = $this->mysystem;				$data['usertypes'] = $this->Admin_mo->getwhere('usertypes',array('active'=>'1','id != '=>'1'));				$data['langs'] = $this->Admin_mo->getwhere('langs',array('active'=>'1'));				$data['user'] = $this->Admin_mo->getrow('users',array('id'=>$id));															$this->load->view('headers/user-edit',$data);				$this->load->view('sidemenu',$data);				$this->load->view('topmenu',$data);				$this->load->view('admin/user-edit',$data);				$this->load->view('footers/user-edit');				$this->load->view('messages');			}			else			{				if($this->Admin_mo->exist('users','where id <> '.$id.' and username like "'.set_value('username').'"',''))				{					$_SESSION['time'] = time(); $_SESSION['message'] = 'usernameexist';					redirect('users/edit/'.$id, 'refresh');				}				if($this->Admin_mo->exist('users','where id <> '.$id.' and email like "'.set_value('email').'"',''))				{					$_SESSION['time'] = time(); $_SESSION['message'] = 'emailexist';					redirect('users/edit/'.$id, 'refresh');				}				else				{					$update_array = array('uid'=>$this->session->userdata('uid'), 'username'=>set_value('username'), 'fname'=>set_value('fname'), 'lname'=>set_value('lname'), 'email'=>set_value('email'), 'utid'=>set_value('usertype'), 'lang'=>set_value('lang'), 'mobile'=>set_value('mobile'), 'active'=>set_value('active'), 'time'=>time());					if(set_value('password') != '' && set_value('password') != ' ')  $update_array['password'] = password_hash(set_value('password'), PASSWORD_BCRYPT, array('cost'=>10));					if($this->Admin_mo->update('users', $update_array, array('id'=>$id)))					{						$_SESSION['time'] = time(); $_SESSION['message'] = 'success';					}					else					{						$_SESSION['time'] = time(); $_SESSION['message'] = 'somthingwrong';					}					redirect('users', 'refresh');				}			}		}		else		{			$_SESSION['time'] = time(); $_SESSION['message'] = 'somthingwrong';			redirect('users', 'refresh');		}		//redirect('users', 'refresh');		}		else		{		$data['title'] = 'users';		$data['admessage'] = 'youhavenoprivls';		$data['system'] = $this->mysystem;		$this->lang->load($this->loginuser->lang, $this->loginuser->lang);		$this->config->set_item('language', $this->loginuser->lang);		$this->load->view('headers/users',$data);		$this->load->view('sidemenu',$data);		$this->load->view('topmenu',$data);		$this->load->view('admin/messages',$data);		$this->load->view('footers/users');		$this->load->view('messages');		}	}	public function del($id)	{		$id = abs((int)($id));		if(strpos($this->loginuser->privileges, ',udelete,') !== false && $id != '1' && in_array('U',$this->sections))		{		$user = $this->Admin_mo->getrow('users', array('id'=>$id));		if(!empty($user))		{			if($user->img != '' && file_exists($this->config->item('users_folder').$user->img)) unlink($this->config->item('users_folder').$user->img);			if(file_exists($this->config->item('users_thumb_folder').$user->img)) unlink($this->config->item('users_thumb_folder').$user->img);			$this->Admin_mo->del('users', array('id'=>$id));			$_SESSION['time'] = time(); $_SESSION['message'] = 'success';			redirect('users', 'refresh');		}		else		{			$data['title'] = 'users';			$data['admessage'] = 'youhavenoprivls';			$data['system'] = $this->mysystem;			$this->lang->load($this->loginuser->lang, $this->loginuser->lang);			$this->config->set_item('language', $this->loginuser->lang);			$this->load->view('headers/users',$data);			$this->load->view('sidemenu',$data);			$this->load->view('topmenu',$data);			$this->load->view('admin/messages',$data);			$this->load->view('footers/users');			$this->load->view('messages');		}		}		else		{		$data['title'] = 'users';		$data['admessage'] = 'youhavenoprivls';		$data['system'] = $this->mysystem;		$this->lang->load($this->loginuser->lang, $this->loginuser->lang);		$this->config->set_item('language', $this->loginuser->lang);		$this->load->view('headers/users',$data);		$this->load->view('sidemenu',$data);		$this->load->view('topmenu',$data);		$this->load->view('admin/messages',$data);		$this->load->view('footers/users');		$this->load->view('messages');		}	}			public function imageSize()	{		if ($_FILES['logo']['size'] > 1024000)		{			//$this->form_validation->set_message('imageSize', 'يجب ان يكون حجم الصورة 1 ميجا او اقل');			return FALSE;		}		else		{			return TRUE;		}	}		public function imageType()	{		if (!in_array(strtoupper(pathinfo($_FILES['logo']['name'], PATHINFO_EXTENSION)),array('JPG','JPEG','PNG','JIF','BMP','TIF')))		{			//$this->form_validation->set_message('imageType', 'يجب ان يكون نوع الملف المرفوع واحد من هذه الانواع : JPG,JPEG,PNG,JIF,BMP,TIF');			return FALSE;		}		else		{			return TRUE;		}	}		public function uploadimg($inputfilename,$image_director,$image_director_thumb,$newname)	{		$file_extn = pathinfo($_FILES[$inputfilename]['name'], PATHINFO_EXTENSION);		if(!is_dir($image_director)) $create_image_director = mkdir($image_director);		$name = $newname.".".$file_extn;		if(move_uploaded_file($_FILES[$inputfilename]["tmp_name"], $image_director.$name))		{			if($image_director_thumb != '')			{				$this->load->library('Resize');				$this->resize->construct($image_director.$name);				$this->resize->resizeImage(210, 210, 'exact');				$this->resize->saveImage($image_director_thumb.$name, 100);			}			return $name;		}		else return '';	}}